import { startUserRequest, endUserRequest } from '../meta/userRequestStatus';
import requestToServer from '../../../modules/requestToServer';
export const ACTION_CHANGE_USER_INFO = 'CHANGE_USER_INFO';
// ミュートユーザーのリストを取得し、ミュート状態のstateを初期化する
export const requestUserInfo = endpoint => dispatch => {
  dispatch(startUserRequest());
  requestToServer(endpoint).then(({
    data
  }) => {
    // console.log(data);
    const userInfo = data; // if ('code' in data[0]) {
    //   dispatch(setErrMessage(data[0].message));
    //   dispatch(endUserRequest());
    //   return;
    // }

    dispatch(endUserRequest()); // ミュートユーザーをstoreに登録

    dispatch(setUserInfo(userInfo));
  });
};
export const setUserInfo = userInfo => ({
  type: ACTION_CHANGE_USER_INFO,
  payload: userInfo
});

const userInfo = (state = {}, action) => {
  switch (action.type) {
    case ACTION_CHANGE_USER_INFO:
      return action.payload;

    default:
      return state;
  }
};

export default userInfo;
